import React from "react";

function MealItem({ nutritions, onDelete, onEdit, isNew }) {
    const getEmojiForFood = (foodName) => {
        const name = foodName.toLowerCase();
        if (name.includes('chicken') || name.includes('meat')) return 'üçó';
        if (name.includes('fish')) return 'üêü';
        if (name.includes('rice') || name.includes('pasta')) return 'üçö';
        if (name.includes('salad') || name.includes('vegetable')) return 'ü•ó';
        if (name.includes('fruit')) return 'üçé';
        if (name.includes('bread') || name.includes('toast')) return 'üçû';
        if (name.includes('egg')) return 'ü•ö';
        if (name.includes('milk') || name.includes('yogurt')) return 'ü•õ';
        return 'üçΩÔ∏è';
    };

    return (
        <div className={`card ${isNew ? 'new-item' : ''}`}>
            <h3>
                {getEmojiForFood(nutritions.itemName)} {nutritions.itemName}
            </h3>

            <div className="nutrition-grid">
                <div className="nutrition-item">
                    <span className="nutrition-label">Calories</span>
                    <span className="nutrition-value">{nutritions.calories}</span>
                    {nutritions.calories > 49 && <span className="limit-exceed">High</span>}
                </div>

                <div className="nutrition-item">
                    <span className="nutrition-label">Protein</span>
                    <span className="nutrition-value">{nutritions.protein}g</span>
                    {nutritions.protein > 30 && <span className="limit-exceed">High</span>}
                </div>

                <div className="nutrition-item">
                    <span className="nutrition-label">Carbs</span>
                    <span className="nutrition-value">{nutritions.carbs}g</span>
                    {nutritions.carbs > 30 && <span className="limit-exceed">High</span>}
                </div>

                <div className="nutrition-item">
                    <span className="nutrition-label">Fat</span>
                    <span className="nutrition-value">{nutritions.fat}g</span>
                    {nutritions.fat > 30 && <span className="limit-exceed">High</span>}
                </div>
            </div>

            <div className="card-buttons">
                <button onClick={onDelete} className="delete-btn">
                    Delete
                </button>
                <button onClick={onEdit} className="edit-btn">
                    Edit
                </button>
            </div>
        </div>
    );
}

export default MealItem;
